        this.inputText = undefined;
        this.onClose = undefined;
        this.delayCall = undefined;
    }

    shutdown(fromScene) {
        // Already shutdown
        if (this.isShutdown) {
            return;
        }

        this.close();
        if (LastOpenedEditor === this) {
            LastOpenedEditor = undefined;
        }

        super.shutdown(fromScene);
    }

